import React,{ useEffect, useState }  from 'react'
import { useNavigate } from 'react-router-dom'
import NavbarComp from '../Navbar/NavbarComp'
import { createAxios, getTokenFromLocalStorage } from '../../Hooks/UseAxios'
import Table from 'react-bootstrap/Table';
import styled, { isStyledComponent } from 'styled-components/macro'
import { ProSidebar, Menu, MenuItem, SubMenu } from 'react-pro-sidebar';
import 'react-pro-sidebar/dist/css/styles.css';
import { Link } from 'react-router-dom';

const Container = styled.section`
  /* border: 1px solid olive; */
  max-width: 1000px;
  margin: 0 auto;
  box-sizing: border-box;
  margin-top: 8rem;
  `

export default function AllTransactions(){
    const navigate = useNavigate()
    const token = getTokenFromLocalStorage()
    const [transactions,setTransactions] = useState()
    const [username,setUserName] = useState("")

  //check user role
  const user = localStorage.getItem('user')
  const parsedUser = JSON.parse(user)
  const userRole = parsedUser?.user?.role[0]?.roleName
  useEffect(() => {
    if (!user || userRole !== 'Admin') {
      navigate('/')
    }
  }, [userRole])

  const fetchAdminData = async () => {
    const token = parsedUser.jwtToken
    // console.log(token)
    const apiUrl = 'http://localhost:8080'
    const authAxios = createAxios(apiUrl, token)

    const data = await authAxios.get('/transactions')
    setTransactions(data?.data)
    //console.log(parsedUser?.user)
  }
  console.log(transactions)
  useEffect(() => {
    if (user && userRole === 'Admin') fetchAdminData()    
  }, [])
  const handleSubmit = async (e) => {
    e.preventDefault()
    //console.log(username)
    const token = parsedUser.jwtToken
    const apiUrl = 'http://localhost:8080'
    const authAxios = createAxios(apiUrl, token)

    const data = await authAxios.get(`/transactions/${username}`)
    //console.log(data)
    setTransactions(data?.data)
  }

  return(
    <>
    <NavbarComp />
    <Container>
    
    <Table striped bordered hover variant="light">
      <thead>
        <tr>
          <th>ID</th>
          <th>Amount</th>
          <th>Beneficiary Account Number</th>
          <th>Created By</th>
          <th>Created Date Time</th>
          <th>Sender Account Number</th>
          <th>Type</th>
        </tr>
      </thead>
      <tbody>
        {/* {transactions === undefined?<>loading...</> : <div>ok</div>} */}
        {transactions?.map((item)=>{
          return(
            <tr key={item.id}>
              <td>{item.id}</td>
              <td>{item.amount}</td>
              <td>{item.benificiaryAccountNumber !== null?item.benificiaryAccountNumber:<>NA</>}</td>
              <td>{item.createdBy}</td>
              <td>{item.createdDateTime}</td>
              <td>{item.senderAccountNumber !== null?item.senderAccountNumber:<>NA</>}</td>
              <td>{item.type}</td>
            </tr>
          )
        })}
      </tbody>
    </Table>
    </Container>
    </>
  )
}
