import React,{ useEffect, useState }  from 'react'
import { useNavigate } from 'react-router-dom'
import NavbarComp from '../Navbar/NavbarComp'
import { createAxios, getTokenFromLocalStorage } from '../../Hooks/UseAxios'
import Table from 'react-bootstrap/Table';
import styled from 'styled-components/macro'
import axios from 'axios';

const Container = styled.section`
  /* border: 1px solid olive; */
  max-width: 1000px;
  margin: 0 auto;
  padding-top: 5rem;
  box-sizing: border-box;
  `

const LeftContainer = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: center;
  padding: 0 1rem;
  align-items: flex-end;
  float: left;
  width: 50%;
  height: 100%;
  margin-top: 8rem;

  @media (max-width: 800px) {
    width: 100%;
  }
`

const RightContainer = styled.div`
  float: right;
  width: 50%;
  display: flex;
  justify-content: center;
  margin-top: 8rem;
  align-items: center;
  /* border: 1px solid purple; */
  height: 100%;
  img {
    width: 100%;
    max-width: 800px;
  }

  @media (max-width: 800px) {
    display: none;
      
  }
`
export default function BookTickets(){
    const navigate = useNavigate()
    const token = getTokenFromLocalStorage()
    const [ticket,setTicket] = useState({
      loginId: '',
      movieName: '',
      theatreName: '',
      noOfTickets: '',
      seatNumber: []
    })
    const [movies, setMovies] = useState([]);

  //check user role
  const user = localStorage.getItem('user')
  const parsedUser = JSON.parse(user)
  const userRole = parsedUser?.roles[0]
  useEffect(() => {
    if (!user || userRole !== 'ROLE_USER') {
      navigate('/')
    }
  }, [userRole])

  const fetchMovies = async () => {
    //  try {
    //   const response = await axios.get('http://localhost:8081/api/v1.0/moviebooking/all')
    //   setMovies(response.data?.data);
    //  } catch(error){
    //   console.log(error);
    //  }
    const accessToken = parsedUser?.accessToken
    //console.log(accessToken)
    const apiUrl = 'http://localhost:8081/api/v1.0/moviebooking'
    const authAxios = createAxios(apiUrl, accessToken)
  
  
    const data = await authAxios.get('/all', {
      validateStatus: function(status){
        return status<500;
      }
    })
    setMovies(data?.data);
    };
  
    useEffect(() => {
       fetchMovies()
    }, [])

  const handleChange = (e) => {
    const { id, value} = e.target;
    if(id === 'seatNumber'){
      const seatNumber = value.split(",").map((num) => num.trim());
      setTicket((prevTicket) => ({...prevTicket, [id]: seatNumber}));
    }else{
      setTicket((prevTicket) => ({ ...prevTicket, [id]: value}));
    }
  };

  const handleSubmit = async (movieName) => {
    // axios
    //   .post(`http://localhost:8081/api/v1.0/moviebooking/${movieName}/add`, ticket)
    //   .then((response) => {
    //     console.log(response.data);
    //     alert("Ticket Booked!")
    //   })
    //   .catch((error) =>{
    //     console.error(error);
    //   });

    const token = parsedUser.accessToken
    console.log(parsedUser)
    const apiUrl = 'http://localhost:8081/api/v1.0/moviebooking'
    const authAxios = createAxios(apiUrl, token)

    const data = await authAxios.post(`/${movieName}/add`, ticket)
    alert('Tickets Booked!');
  };

  return(
    <>
    <NavbarComp />
    <RightContainer>
    <h2 className='mt-5'>Book Ticket</h2>
        <form className='row g-3 mt-5' noValidate>
        <div className='col-md-6'>
            <label htmlFor='loginId' className='form-label'>
              Login ID
            </label>
            <input
              type='text'
              className='form-control'
              id='loginId'
              value={ticket.loginId}
              onChange={handleChange}
              required
            />
          </div>
          <div className='col-md-6'>
            <label htmlFor='movieName' className='form-label'>
              Movie Name
            </label>
            <input
              type='text'
              className='form-control'
              id='movieName'
              value={ticket.movieName}
              onChange={handleChange}
              required
            />
            <div className='valid-feedback'>Looks good!</div>
          </div>
          <div className='col-md-6'>
            <label htmlFor='theatreName' className='form-label'>
              Theatre Name
            </label>
            <input
              type='text'
              className='form-control'
              id='theatreName'
              value={ticket.theatreName}
              onChange={handleChange}
              required
            />
          </div>
          <div className='col-md-6'>
            <label htmlFor='noOfTickets' className='form-label'>
              Number of Tickets
            </label>
            <input
              type='text'
              className='form-control'
              id='noOfTickets'
              value={ticket.noOfTickets}
              onChange={handleChange}
              required
            />
          </div>
          <div className='col-md-6'>
            <label htmlFor='seatNumber' className='form-label'>
              Seat Numbers
            </label>
            <input
              type='text'
              className='form-control'
              value={ticket.seatNumber}
              id='seatNumber'
              onChange={handleChange}
              required
            />
          </div>

          {/* <ErrorBox>{error && errorMessage}</ErrorBox> */}
          <div className='col-12'>
            <button
              type='submit'
              className='btn btn-primary'
              onClick={() => handleSubmit(ticket.movieName)}
            >
              Book
            </button>
          </div>
        </form>
    </RightContainer>
    <LeftContainer>
    <Table striped bordered hover variant='light'>
          <thead>
            <tr>
              <th>Movie Name</th>
              <th>Theatre Name</th>
              <th>Number Of Tickets Available</th>
              <th>Tickets Status</th>
            </tr>
          </thead>
          <tbody>
            {movies?.map((item,index) => {
              return (
                <tr key={index}>
                  <td>{item.movieName}</td>
                  <td>{item.theatreName}</td>
                  <td>{item.noOfTicketsAvailable}</td>
                  <td>{item.ticketsStatus}</td>
                </tr>
              )
            })}
          </tbody>
        </Table>
      </LeftContainer>
    </>
  )
}
