import React,{ useEffect }  from 'react'
import styled from 'styled-components/macro'
import { useNavigate } from 'react-router-dom'
import NavbarComp from '../Navbar/NavbarComp'
import { createAxios, getTokenFromLocalStorage } from '../../Hooks/UseAxios'
import ListGroup from 'react-bootstrap/ListGroup';

const Container = styled.section`
  /* border: 1px solid olive; */
  padding-top: 5rem;
  max-width: 1000px;
  margin: 0 auto;
  box-sizing: border-box;
`

export default function UserDashboard(){
    const navigate = useNavigate()
    const token = getTokenFromLocalStorage()

  //check user role
  const user = localStorage.getItem('user')
  const parsedUser = JSON.parse(user)
  const userRole = parsedUser?.user?.role[0]?.roleName
  useEffect(() => {
    if (!user || userRole !== 'User') {
      navigate('/')
    }
  }, [userRole])

  const fetchAdminData = async () => {
    const token = parsedUser.jwtToken
    // console.log(token)
    const apiUrl = 'http://localhost:8080'
    const authAxios = createAxios(apiUrl, token)

    const data = await authAxios.get('/forUser')

    console.log(parsedUser?.user)
  }
  useEffect(() => {
    if (user && userRole === 'User') fetchAdminData()    
  }, [])

  return(
    <>
    <NavbarComp />
    <Container>
    
    <ListGroup>
    <div className='col-12'>
      <ListGroup.Item>Name: {parsedUser?.user?.userFirstName} {parsedUser?.user?.userLastName}</ListGroup.Item>
      <ListGroup.Item>Aadhar Number: {parsedUser?.user?.aadharNumber}</ListGroup.Item>
      <ListGroup.Item>Email Id: {parsedUser?.user?.emailId}</ListGroup.Item>
      <ListGroup.Item>Phone Number: {parsedUser?.user?.phoneNumber}</ListGroup.Item>
      <ListGroup.Item>Gender: {parsedUser?.user?.gender}</ListGroup.Item>
      {/* <ListGroup.Item>DOB: {parsedUser?.user?.dateOfBirth}</ListGroup.Item> */}
      <ListGroup.Item>Account Number: {parsedUser?.user?.accountNumber}</ListGroup.Item>
      <ListGroup.Item>Account Type: {parsedUser?.user?.typeOfAccount}</ListGroup.Item>
      <ListGroup.Item>Bank Name: {parsedUser?.user?.bankName}</ListGroup.Item>
      <ListGroup.Item>Bank Id: {parsedUser?.user?.bankId}</ListGroup.Item>
    </div>
    </ListGroup>
    </Container>
    </>
  )
}
