import React, { useContext, useEffect, useState } from 'react'
import styled from 'styled-components/macro'
import axios from 'axios'
import { useNavigate } from 'react-router-dom'
import userContext from '../../Context/userContext'
import NavbarComp from '../Navbar/NavbarComp'
import { Button, Col, Container, Form, Row } from 'react-bootstrap'

const LoginContainer = styled.main`
  /* border: 1px solid red; */
  height: 90vh;
`

const ErrorBox = styled.div`
  height: 30px;
  margin: 1rem 0;
  color: red;
  text-align: center;
`
const LeftContainer = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: center;
  padding: 0 1rem;
  align-items: flex-end;
  float: left;
  width: 50%;
  height: 100%;

  @media (max-width: 800px) {
    width: 100%;
  }
`
const Footer = styled.footer`
  background-color: #212529;
  height: 3.6vh;
`
const FormWrapper = styled.div`
  width: 60%;

  @media (max-width: 800px) {
    width: 100%;
  }
`

const RightContainer = styled.div`
  float: right;
  width: 50%;
  display: flex;
  justify-content: center;
  align-items: center;
  /* border: 1px solid purple; */
  height: 100%;
  img {
    width: 100%;
    max-width: 800px;
  }

  @media (max-width: 800px) {
    display: none;
      
  }
`

export default function Login() {
  const [formData, setFormData] = useState({
    userName: '',
    userPassword: '',
  })

  const { userName, userPassword } = formData

  const [error, setError] = useState(false)
  const [errorMessage, setErrorMessage] = useState('')

  let navigate = useNavigate()

  const { userData, setUserData } = useContext(userContext)

  const onChange = (e) => {
    setFormData((prevState) => ({
      ...prevState,
      [e.target.name]: e.target.value,
    }))
  }

  const handleSubmit = async (e) => {
    e.preventDefault()

    if (!userName || !userPassword) {
      setError(true)
      setErrorMessage('Fields cannot be empty!')
    }
    if (userName && userPassword) {
      setError(false)
      const URL = 'http://localhost:8080/authenticate'
      const postData = { userName: userName, userPassword: userPassword }

      await axios
        .post(URL, postData)
        .then((response) => {
          setError(false)
          console.log(response.data)
          const jwtToken = response.data.jwtToken
          const role = response.data.user.role[0].roleName
          if (jwtToken) {
            const user = { jwtToken, role }
            setUserData(user)
            localStorage.setItem('user', JSON.stringify(response.data))
            // navigate('/dashboard')
            if (user?.role === 'Admin') navigate('/forAdmin')
            if (user?.role === 'User') navigate('/forUser')
            console.log(user?.role)
          }
        })
        .catch((error) => {
          setError(true)
          setErrorMessage('Invalid Credentials!')
          setFormData({ userPassword: '' })
          console.log(error)
        })
    }
  }

  useEffect(() => {
    if (localStorage.getItem('user')) {
      setUserData(localStorage.getItem('user'))
    }
  }, [userData])

  return (
    <>
      <NavbarComp />
      <LoginContainer>
        <LeftContainer>
          <FormWrapper>
            <Form>
              <Form.Group controlId='formBasicEmail' className='mb-4'>
                <Form.Label>Username</Form.Label>
                <Form.Control
                  type='text'
                  placeholder='Username'
                  name='userName'
                  value={userName || ''}
                  onChange={onChange}
                  required
                />
              </Form.Group>

              <Form.Group controlId='formBasicPassword'>
                <Form.Label>Password</Form.Label>
                <Form.Control
                  type='password'
                  placeholder='Password'
                  name='userPassword'
                  value={userPassword || ''}
                  onChange={onChange}
                  required
                />
              </Form.Group>
              <ErrorBox>{errorMessage}</ErrorBox>
              <div className='d-grid '>
                <Button
                  className='btn btn-primary'
                  type='button'
                  onClick={handleSubmit}
                >
                  Login
                </Button>
              </div>
            </Form>
          </FormWrapper>
        </LeftContainer>

        <RightContainer>
          <img src='login.png' alt='logo' />
        </RightContainer>
      </LoginContainer>
      <Footer></Footer>
    </>
  )
}
