import React, { useEffect, useState } from 'react'
import { useNavigate } from 'react-router-dom'
import NavbarComp from '../Navbar/NavbarComp'
import { createAxios, getTokenFromLocalStorage } from '../../Hooks/UseAxios'
import Table from 'react-bootstrap/Table'
import styled from 'styled-components/macro'
import 'react-pro-sidebar/dist/css/styles.css'
import Form from 'react-bootstrap/Form'
import axios from 'axios'

const ErrorBox = styled.div`
  height: 30px;
  margin: 1rem 0;
  /* border: 1px solid red; */
  color: red;
  width: 100%;
  padding: 2px 0;
  text-align: center;
`

const Container = styled.section`
  /* border: 1px solid olive; */
  max-width: 1000px;
  margin: 0 auto;
  box-sizing: border-box;
  margin-top: 5rem;
  min-height:82vh ;
`

const Footer = styled.footer`
  background-color: #212529;
  height: 3.6vh;
`

export default function AddMovieForm() {
  const navigate = useNavigate()
  const token = getTokenFromLocalStorage()
  const [error, setError] = useState(false)
  const [errorMessage, setErrorMessage] = useState('')

  //check user role
  const user = localStorage.getItem('user')
  const parsedUser = JSON.parse(user)
  const userRole = parsedUser?.roles[0]
  useEffect(() => {
    if (!user || userRole !== 'ROLE_ADMIN') {
      navigate('/')
    }
  }, [userRole])

  const [movie, setMovie] = useState({
    movieName: '',
    theatreName: '',
    noOfTicketsAvailable: 0,
    ticketsStatus: ''
  });

  const handleChange = (e) => {
    const {movieName, value} = e.target;
    setMovie((prevMovie) => ({...prevMovie, [movieName]: value}));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    axios
      .post('http://localhost:8081/api/v1.0/moviebooking/savemovie', movie)
      .then((response) => {
        console.log(response.data);
      })
      .catch((error) => {
        console.log(error);
      })
  };

  return (
    <>
      <NavbarComp />
      <Container>
        <Form>
          <Form.Group className='col-md-6' style={{ display: 'inline-block' }}>
            <Form.Label>Movie Name</Form.Label>
            <Form.Control
              type='text'
              value={movie.movieName}
              onChange={(e) => {
                setMovie(e.target.value)
                setError(false)
                setErrorMessage('')
              }}
            />
            <Form.Label>Theatre Name</Form.Label>
            <Form.Control
              type='text'
              value={movie.theatreName}
              onChange={handleChange}
            />
            <Form.Label>Number Of Tickets Available</Form.Label>
            <Form.Control
              type='number'
              value={movie.noOfTicketsAvailable}
              onChange={handleChange}
            />
            <Form.Label>Ticket Status</Form.Label>
            <Form.Control
              type='text'
              value={movie.ticketsStatus}
              onChange={handleChange}
            />
          </Form.Group>

          <button
            type='submit'
            className='btn btn-primary'
            onClick={handleSubmit}
            style={{marginLeft:'5rem'}}
          >
            Add Movie
          </button>

          <ErrorBox>{error && errorMessage}</ErrorBox>
        </Form>
      </Container>
      <Footer></Footer>
    </>
  )
}
