import Card from 'react-bootstrap/Card'
import React, { useEffect, useState } from 'react'
import { useNavigate, Outlet } from 'react-router-dom'
import NavbarComp from '../Navbar/NavbarComp'
import { createAxios, getTokenFromLocalStorage } from '../../Hooks/UseAxios'
import Table from 'react-bootstrap/Table'
import styled, { isStyledComponent } from 'styled-components/macro'
import { ProSidebar, Menu, MenuItem, SubMenu } from 'react-pro-sidebar'
import 'react-pro-sidebar/dist/css/styles.css'
import { Link } from 'react-router-dom'

const Container = styled.section`
  /* border: 1px solid olive; */
  max-width: 1000px;
  margin: 0 auto;
  box-sizing: border-box;
  margin-top: 8rem;
`

export default function AllMovie() {
  const navigate = useNavigate()
  const token = getTokenFromLocalStorage()
  const [error, setError] = useState(false)
  const [errorMessage, setErrorMessage] = useState('')

  //check user role
  const user = localStorage.getItem('user')
  const parsedUser = JSON.parse(user)
  const userRole = parsedUser?.roles[0]
  useEffect(() => {
    if (!user || userRole !== 'ROLE_ADMIN') {
      navigate('/')
    }
  }, [userRole])

  const [movies, setMovies] = useState([]);

  // useEffect(() => {
  //   fetchMovies();
  // }, []);

  const fetchMovies = async () => {
   try {
    const response = await axios.get('http://localhost:8081/api/v1.0/moviebooking/all')
    setMovies(response.data);
   } catch(error){
    console.log(error);
   }

  };

  useEffect(() => {
    if (user && userRole === 'ROLE_ADMIN') fetchMovies()
  }, [])

  return (
    <>
      <NavbarComp />
      <Container>
        <Table striped bordered hover variant='light'>
          <thead>
            <tr>
              <th>Movie Name</th>
              <th>Theatre Name</th>
              <th>Number Of Tickets Available</th>
              <th>Tickets Status</th>
            </tr>
          </thead>
          <tbody>
            {movies?.map((item) => {
              return (
                <tr key={item.movieName}>
                  <td>{item.movieName}</td>
                  <td>{item.theatreName}</td>
                  <td>{item.noOfTicketsAvailable}</td>
                  <td>{item.ticketsStatus}</td>
                </tr>
              )
            })}
          </tbody>
        </Table>
      </Container>
    </>
  )
}
