import Card from 'react-bootstrap/Card'
import React, { useEffect, useState } from 'react'
import { useNavigate, Outlet } from 'react-router-dom'
import NavbarComp from '../Navbar/NavbarComp'
import { createAxios, getTokenFromLocalStorage } from '../../Hooks/UseAxios'
import Table from 'react-bootstrap/Table'
import styled, { isStyledComponent } from 'styled-components/macro'
import { ProSidebar, Menu, MenuItem, SubMenu } from 'react-pro-sidebar'
import 'react-pro-sidebar/dist/css/styles.css'
import { Link } from 'react-router-dom'

const Container = styled.section`
  /* border: 1px solid olive; */
  max-width: 1000px;
  margin: 0 auto;
  box-sizing: border-box;
  margin-top: 8rem;
`

export default function AllUsers() {
  const navigate = useNavigate()
  const token = getTokenFromLocalStorage()
  const [transactions, setTransactions] = useState()

  //check user role
  const user = localStorage.getItem('user')
  const parsedUser = JSON.parse(user)
  const userRole = parsedUser?.user?.role[0]?.roleName
  useEffect(() => {
    if (!user || userRole !== 'Admin') {
      navigate('/')
    }
  }, [userRole])

  const fetchAdminData = async () => {
    const token = parsedUser.jwtToken
    // console.log(token)
    const apiUrl = 'http://localhost:8080'
    const authAxios = createAxios(apiUrl, token)

    const data = await authAxios.get('/users')
    setTransactions(data?.data)
    //console.log(parsedUser?.user)
  }
  console.log(transactions)
  useEffect(() => {
    if (user && userRole === 'Admin') fetchAdminData()
  }, [])

  return (
    <>
      <NavbarComp />
      <Container>
        <Table striped bordered hover variant='light'>
          <thead>
            <tr>
              <th>Account Number</th>
              <th>First Name</th>
              <th>Last Name</th>
              <th>Username</th>
              <th>Balance</th>
              <th>Type</th>
            </tr>
          </thead>
          <tbody>
            {/* {transactions === undefined?<>loading...</> : <div>ok</div>} */}
            {transactions?.map((item) => {
              return (
                <tr key={item.accountNumber}>
                  <td>{item.accountNumber}</td>
                  <td>{item.userFirstName}</td>
                  <td>{item.userLastName}</td>
                  <td>{item.userName}</td>
                  <td>{item.balance}</td>
                  <td>{item.typeOfAccount}</td>
                </tr>
              )
            })}
          </tbody>
        </Table>
      </Container>
    </>
  )
}
