import React, { useState } from 'react'
import NavbarComp from '../Navbar/NavbarComp'
import styled from 'styled-components/macro'
import { createAxios, getTokenFromLocalStorage } from '../../Hooks/UseAxios'
import { useNavigate } from 'react-router-dom'

const Container = styled.section`
  /* border: 1px solid olive; */
  max-width: 1000px;
  margin: 0 auto;
  padding-top: 5rem;
  box-sizing: border-box;
`
const ErrorBox = styled.div`
  height: 30px;
  margin: 1rem 0;
  /* border: 1px solid red; */
  color: red;
  width: 100%;
  text-align: left;
`

export default function Withdraw() {
  const token = getTokenFromLocalStorage()
  const user = localStorage.getItem('user')
  const parsedUser = JSON.parse(user)
  const [error, setError] = useState(false)
  const [errorMessage, setErrorMessage] = useState('')
  const [formData, setFormData] = useState({
    benificiaryAccountNumber: parsedUser.user.accountNumber,
    amount: '',
  })
  const navigate = useNavigate()
  const onChange = (e) => {
    setFormData((prevState) => ({
      ...prevState,
      [e.target.id]: e.target.value,
    }))
    setError(false)
    setErrorMessage("")
  }


  const submitForm = async (e) => {
    const apiUrl = 'http://localhost:8080'
    const authAxios = createAxios(apiUrl, token)

    const data = await authAxios.post('/withdraw', formData)

    if (data.data === 'Insufficient Balance') {
      setError(true)
      setErrorMessage('Insufficient Balance')
    } else {
      window.alert('Withdrawn Successfully')
      navigate(-1)
    }
    console.log(data)
  }

  const handleSubmit = (e) => {
    e.preventDefault()
    console.log(formData)
    if (!formData.amount) {
      setError(true)
      setErrorMessage('Field cannot be empty!')
      console.log('we')
      return
    } else submitForm()
  }
  return (
    <>
      <NavbarComp />
      <Container>
        <h2>Withdraw Funds</h2>
        <form className='row g-3 mt-5' noValidate>
          <div className='col-md-6'>
            <label htmlFor='amount' className='form-label'>
              Amount
            </label>
            <input
              type='text'
              className='form-control'
              id='amount'
              onChange={onChange}
              required
            />
          </div>
          <ErrorBox>{error && errorMessage}</ErrorBox>
          <div className='col-12'>
            <button
              type='submit'
              className='btn btn-primary'
              onClick={handleSubmit}
            >
              Withdraw
            </button>
          </div>
        </form>
      </Container>
    </>
  )
}
